/**
 * Test script for structure JSX generator
 */

const fs = require('fs');
const path = require('path');
const { generateStructureJsx } = require('./structure-jsx-generator');

// Load structure mapping
const structureMappingPath = path.join(
  __dirname,
  '../../schemas/accordion-structure-mapping-autogenerated.json'
);

const structureMapping = JSON.parse(fs.readFileSync(structureMappingPath, 'utf8'));

console.log('='.repeat(80));
console.log('STRUCTURE JSX GENERATOR TEST');
console.log('='.repeat(80));
console.log();

// Generate for save mode
console.log('SAVE MODE OUTPUT:');
console.log('='.repeat(80));
const saveCode = generateStructureJsx(structureMapping, 'save');
console.log(saveCode);
console.log();

// Generate for edit mode
console.log('='.repeat(80));
console.log('EDIT MODE OUTPUT:');
console.log('='.repeat(80));
const editCode = generateStructureJsx(structureMapping, 'edit');
console.log(editCode);
console.log();

console.log('='.repeat(80));
console.log('Test completed successfully!');
console.log('='.repeat(80));
